package Program1;


public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=25,b=10,c;
		
		c=a+b;
		System.out.println(c);
		
		c=a-b;
		System.out.println(c);
		
		c=a*b;
		System.out.println(c);
		
		c=a%b;
		System.out.println(c);
		
		c=a/b;
		System.out.println(c);	
	}
}
