package Program1;

public class TypeConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//implicit type conversion
		byte b =5;
		int n;
		n=b;
		System.out.println(n);
		//explicit type conversion
		byte x;
		int y=10;
		x=(byte)y;
		System.out.println(x);
				

	}

}
